import React, { useState, useEffect} from "react";
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardHeader from '@material-ui/core/CardHeader';
import axios from "axios";
import { ChartDonut, ChartThemeColor} from '@patternfly/react-charts';
import CardContent from '@material-ui/core/CardContent';
import Avatar from '@material-ui/core/Avatar';
import IconButton from '@material-ui/core/IconButton';
import Typography from '@material-ui/core/Typography';
import { red } from '@material-ui/core/colors';
import ClearIcon from '@material-ui/icons/Clear';
import Grid from "@material-ui/core/Grid";



const useStyles = makeStyles((theme) => ({
  root: {
    maxWidth: 345,
  },
  media: {
    height: 0,
    paddingTop: '56.25%', // 16:9
  },
  expand: {
    transform: 'rotate(0deg)',
    marginLeft: 'auto',
    transition: theme.transitions.create('transform', {
      duration: theme.transitions.duration.shortest,
    }),
  },
  expandOpen: {
    transform: 'rotate(180deg)',
  },
  avatar: {
    backgroundColor: red[500],
  },
}));

export default function Roboterstatus() {

const [allData, setAllData] = useState([]); 
const [cardData, setCardData] = useState([])


useEffect(() => {
  const interval = setInterval(() => {
    getRobotsStates(); 
  }, 3000);

  return () => clearInterval(10000); //alle 10 Sekunden
}, [])

function getRobotsStates(){

    axios.get('http://0.0.0.0:8080/robot_list')
    .then(res => {
    console.log("Robotstates:", res.data); //Data from Gateway

    if(res.data.length === 0) { //Check if data is available
      //Karten auf null setzen
      return;
    }

    setAllData(res.data); //Set new table data
    setCardData(res.data);

    })
    .catch(err => {
        console.log(err.message); //Error-Handling
    })
}

const useStyles = makeStyles((theme) => ({
    root: {
      flexGrow: 1,
      padding: theme.spacing(2)
    }
  }));

    const data = {
      name: cardData


      /*[
        { quarter: 1, earnings: 13000 },
        { quarter: 2, earnings: 16500 },
        { quarter: 3, earnings: 14250 },
        { quarter: 4, earnings: 19000 },
        {quarter: 2, earnings: 16500 },
        { quarter: 3, earnings: 14250 },
        { quarter: 4, earnings: 19000 },

      ] */
    };


  const classes = useStyles();

  return (
    <div className={classes.root}>
      <Grid
        container
        spacing={2}
        direction="row"
        justify="flex-start"
        alignItems="flex-start">

        {data.name.map((elem) => (
          <Grid item xs={3} key={cardData.name.indexOf(elem)}>
            <Card>
              <CardHeader
                title={`quarter : ${elem.quarter}`}
                subheader={`earnings : ${elem.earnings}`}
              />
              <CardContent>
                <Typography variant="h5" gutterBottom>
                  Hello World
                </Typography>
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>
    ))
  </div>
  );
}
