import React, { useState, useEffect} from "react";
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardHeader from '@material-ui/core/CardHeader';
import axios from "axios";
import { ChartDonut, ChartThemeColor} from '@patternfly/react-charts';
import CardContent from '@material-ui/core/CardContent';
import Avatar from '@material-ui/core/Avatar';
import IconButton from '@material-ui/core/IconButton';
import Typography from '@material-ui/core/Typography';
import { red } from '@material-ui/core/colors';
import ClearIcon from '@material-ui/icons/Clear';
import Grid from "@material-ui/core/Grid";



const useStyles = makeStyles((theme) => ({
  root: {
    maxWidth: 345,
  },
  media: {
    height: 0,
    paddingTop: '56.25%', // 16:9
  },
  expand: {
    transform: 'rotate(0deg)',
    marginLeft: 'auto',
    transition: theme.transitions.create('transform', {
      duration: theme.transitions.duration.shortest,
    }),
  },
  expandOpen: {
    transform: 'rotate(180deg)',
  },
  avatar: {
    backgroundColor: red[500],
  },
}));

export default function Roboterstatus() {

const [allData, setAllData] = useState([]); 
const [cardData, setCardData] = useState([])


/*useEffect(() => {
  const interval = setInterval(() => {
    getRobotsStates(); 
  }, 3000);

  return () => clearInterval(10000); //alle 10 Sekunden
}, []) */

useEffect(() => {getRobotsStates();});

function getRobotsStates(){

    axios.get('http://0.0.0.0:8080/robot_list')
    .then(res => {
    console.log("Robotstates:", res.data); //Data from Gateway

    if(res.data.length === 0) { //Check if data is available
      //Karten auf null setzen
      return;
    }

    setAllData(res.data); //Set new table data
    setCardData(res.data);

    })
    .catch(err => {
        console.log(err.message); //Error-Handling
    })
}

const useStyles = makeStyles((theme) => ({
    root: {
      flexGrow: 1,
      padding: theme.spacing(2)
    }
  }));

  const classes = useStyles();

  return (
    <div className={classes.root}>
      <Grid
        container
        spacing={2}
        direction="row"
        justify="flex-start"
        alignItems="flex-start">

        {cardData.map((elem) => (
          <Grid item xs={3} key={cardData.indexOf(elem)}>
            <Card>
              <CardHeader
              avatar={
                <Avatar aria-label="recipe" className={classes.avatar}>
                  R
                </Avatar>
              }
              action={
                <IconButton aria-label="settings">
                  <ClearIcon />
                </IconButton>
              }
                title={`Batterie: ${elem['battery_percent']}`}
                subheader={`Fleet_name : ${elem['fleet_name']}`}
              />

              <CardContent>
              <ChartDonut
                ariaDesc="Batteriestatus"
                ariaTitle="Batteriestatus"
                constrainToVisibleArea={true}
                data={[{ x: '', y: elem['battery_percent']}, {x: '', y: 100-elem['battery_percent'] }]}
                height={90}
                labels={({ datum }) => `${datum.x} ${datum.y}%`}
                //themeColor={ }
                title= {Math.round(elem['battery_percent'], 2) + "%"}
                padding={{
                bottom: 0,
                left: 0,
                right: 0, 
                top: 0
                }}
                width={140}/>
                <Typography variant="h5" gutterBottom>
                  Weitere Infos
                </Typography>
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>
    ))
  </div>
  );
}
