    getSessionId(); //Get sessionID

    axios.get('http://0.0.0.0:8080/task_list')
    .then(res => {
    console.log("Tasks:", res.data); //Data from Gateway

    if(res.data.length === 0) { //Check if data is available

      return;
    }

