            alert("Fehler beim Auftrag übermitteln.");  

        })  

    });

  } 


  //Check if old data = new data
  function DataAreEqual(inputData, sortedOrders){
    if(inputData.sort().join(',') === sortedOrders.sort().join(',')){
      return true;
      }
      else return false;
   }


